import React, { useState, useEffect } from 'react';
import './bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import SearchBox from './components/SearchBox';
import AddFavourite from './components/AddFavourite';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [favourites,setFavourites] = useState([]);
    const [searchValue,setSearchValue] = useState('');
    const getMovieRequest = async () =>{
      const url = `http://www.omdbapi.com/?s=${searchValue}&apikey=b7bc2d0c`;
      const response = await fetch(url);
      const responseJson = await response.json();
      if(responseJson.Search){
        setMovies(responseJson.Search);
      }

    };

    useEffect(()=>{
      getMovieRequest();
  },[searchValue]);

  const addFavouriteMovie = (movie) =>{
    const newFavouriteList = [...favourites,movie];
    setFavourites(newFavouriteList);
  }
    return ( < div className = 'container-fluid movie-app' >
        <div className='row d-flex align-items-center mt-4 mb-4'>
          <MovieListHeading heading='Movies'></MovieListHeading>
          <SearchBox searchValue={searchValue} setSearchValue={setSearchValue}></SearchBox>
        </div>
        <div className = 'row'>
          <MovieList movies={movies} handleFavouritesClick={addFavouriteMovie} FavouriteComponent={AddFavourite}></MovieList>
        </div> 
        <div className='row d-flex align-items-center mt-4 mb-4'>
          <MovieListHeading heading='Movies'></MovieListHeading>

        </div>
        </div >
    );
};
export default App;
